const lf = new LogicFlow({
  container: document.querySelector('#app'),
  grid: true,
  width: 1400,
  keyboard: {
    enabled: true
  },
  plugins: [Group, Control, DndPanel],
  height: 800
})


class MyGroup extends GroupNode.view {}

class MyGroupModel extends GroupNode.model {
  initNodeData(data) {
    super.initNodeData(data);
    this.isRestrict = true;
    this.resizable = true;
    this.width = 480;
    this.height = 280;
  }
  getNodeStyle() {
    const style = super.getNodeStyle();
    style.stroke = "#AEAFAE";
    style.strokeWidth = 1;
    return style;
  }
}

class MyGroup1 extends GroupNode.view {}

class MyGroupModel1 extends GroupNode.model {
  setAttributes() {
    const size = 80;
    const circleOnlyAsTarget = {
      message: "正方形节点下一个节点只能是圆形节点",
      validate: (sourceNode, targetNode, sourceAnchor, targetAnchor) => {
        return false;
      },
    };
    this.targetRules.push(circleOnlyAsTarget);
  }
  initNodeData(data) {
    super.initNodeData(data);
    this.foldable = true;
    this.width = 400;
    this.height = 200;
  }
  getNodeStyle() {
    const style = super.getNodeStyle();
    style.stroke = "#989891";
    style.strokeWidth = 1;
    style.strokeDasharray = "3 3";
    if (this.isSelected) {
      style.stroke = "rgb(124, 15, 255)";
    }
    if (this.isFolded) {
      style.fill = "#47C769";
    }
    return style;
  }
}


const rect = {
  type: 'rect',
  label: 'rect',
  icon: 'data:image/png;base64,',
}
const circle = {
  type: 'circle',
  label: 'circle',
  icon: 'data:image/png;base64,',
};
console.log(lf.extension)
lf.extension.dndPanel.setPatternItems([
  rect,
  circle,
]);

lf.register({
  type: 'my-group',
  model: MyGroupModel,
  view: MyGroup
})
lf.register({
  type: "sub-process",
  model: MyGroupModel1,
  view: MyGroup1
});


lf.render({
  nodes: [
    {
      type: "my-group",
      x: 400,
      y: 400,
      children: ["rect_2"]
    },
    {
      id: "rect_2",
      type: "circle",
      x: 400,
      y: 400
    },
    {
      id: "rect_3",
      type: "rect",
      x: 200,
      y: 100
    },
    {
      id: "circle_4",
      type: "circle",
      x: 800,
      y: 140
    },
    {
      id: "group_2",
      type: "sub-process",
      x: 300,
      y: 120,
      children: ["rect_3"],
      properties: {
        isFolded: true
      }
    },
    {
      id: "group_4",
      type: "sub-process",
      x: 800,
      y: 120,
      children: ["circle_4"],
      properties: {
        isFolded: true
      }
    }
  ]
});


